conn system /123
create user ocp identified by 123;
grant connect,RESOURCE to ocp;
conn ocp/123;
Drop table Employees;
Drop table Formtion;
Drop table Plan_formation;
Drop table Evaluation_Finance;
create table Employees (Matricule Varchar2(8) primary key,Nom varchar2(30) not null,Prenom varchar2(30) not null,Site varchar2(30),Date_naissance Date , Sexe number(1),Date_recrutement Date, Departement varchar2(30) not null ,Csp varchar2(30) not null,Fonction varchar2(30));
create table Formation (Code_formation number(8) PRIMARY KEY,Date_debut Date , Duree number(3),Nb_heurs number(4),Prestataire varchar2(30),Intitule_formation varchar2(30) not null,Type_formation varchar2(30) UNIQUE , Competence_visee varchar2(50) not null,Parcours varchar2(30),Objectifs varchar2(50),Formateur Varchar2(28),cout_total number(10,3));
create table Plan_formation (Id number(8) primary key,Code_formation number(8),Constraint F_fk foreign key(Code_Formation) references Formation(Code_formation),Matricule Varchar2(8) ,Constraint EE_fk foreign key(Matricule) references Employees(Matricule),Presence number(4),Note number(2),Performance varchar2(50) ,Commentaire varchar2(100));
alter table Employees add constraint check_sexe check(Sexe=1 or Sexe=0);
alter table Employees add constraint check_date check (Date_naissance > sysdate) ;
create table login(username varchar2(40) primary key , mdp varchar(30) not null);
create sequence seq_formation increment by 1 start WITH 3 ;
create sequence seq_Plan_formation increment by 1 start WITH 1 ;
COMMIT;
commit;
